async def get_hidjra(user_id):
	url = "https://aladhan.p.rapidapi.com/timingsByAddress"
	querystring = {"address":'–ö–∞–∑–∞–Ω—å'}
	headers = {
		"X-RapidAPI-Key": "fa3e8dc2dbmshd8f35322ed30bb0p1179d0jsn655fe6d43bde",
		"X-RapidAPI-Host": "aladhan.p.rapidapi.com"
	}
	response = requests.request("GET", url, headers=headers, params=querystring).json()
	date = response['data']['date']['hijri']['date'].replace('-', '.')
	month = response['data']['date']['hijri']['month']['ar']


	return date + ' | ' + month

